const SEND_MESSAGE = 'SEND-MESSAGE';
const UPDATE_NEW_DIALOG_TEXT = 'UPDATE-NEW-DIALOG-TEXT';

let initialState = {
  dialogs: [
    {name: 'Andrey', id: 1, message: ''},
    {name: 'Vika', id: 2, message: ''},
    {name: 'Seryi', id: 3, message: ''},
    {name: 'Kolya', id: 4, message: ''}
  ],
  avatars: [
    {
      avatar: 'data:image/jpeg;base64,',
      alt: 'ava-1'
    },
    {
      avatar: 'data:image/jpeg;base64,',
      alt: 'ava-2'
    },
    {
      avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSaqiRAB9a6nxIGBFSUKbjw2IqbEjpzuOKyAA&usqp=CAU',
      alt: 'ava-3'
    },
    {
      avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTMpxngDzweK9PjrcBaBt1eTTL6dymJAz1Tjw&usqp=CAU',
      alt: 'ava-4'
    }
  ],
  newDialogText: ''
};

const dialogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SEND_MESSAGE:
      let newMessage = {
        name: 'Vasya',
        id: 1,
        message: state.newDialogText,
      }
      return {
        ...state,
        dialogs: [...state.dialogs, newMessage],
        newDialogText: ''
      }

    case UPDATE_NEW_DIALOG_TEXT:
      return  {
        ...state,
        newDialogText: action.newText
      }

    default:
      return state;
  }
}

export const sendMessageActionCreator = () => ({type: SEND_MESSAGE});
export const updateNewDialogTextActionCreator = (text) => ({type: UPDATE_NEW_DIALOG_TEXT, newText: text});

export default dialogsReducer;